def find_motif_location(text, pattern):
    motif_locations = []
    for i in range(len(text) - len(pattern) - 1):
        if text[i:i+len(pattern)] == pattern:
            motif_locations.append(i+1) # for 1-based start instead of 0-based
    return motif_locations

text = ""
pattern = "CCGGTGCCC"
print(find_motif_location(text, pattern))